  CREATE OR REPLACE FORCE EDITIONABLE VIEW "DISCRT"."DISCRTV_DOWNTIME_TORQUE_CURRENT" ("PROD_DATE", "LINE", "DOWNTIME_MINS", "DOWNTIME_HRS", "REASON", "DOWNTIME_TYPE", "START_TIME", "END_TIME", "DOWNTIME_UOM", "FACTORY_LOC", "CATEGORY_ID", "DOWNTIME_CATEGORY", "MAINDRIVE1_TORQUE_PCT", "MAINDRIVE1_CURRENT", "MAINDRIVE2_TORQUE_PCT", "MAINDRIVE2_CURRENT") DEFAULT COLLATION "USING_NLS_COMP"  AS 
  select d."PROD_DATE",d."LINE",d."DOWNTIME_MINS",d."DOWNTIME_HRS",d."REASON",d."DOWNTIME_TYPE",d."START_TIME",d."END_TIME",d."DOWNTIME_UOM",d."FACTORY_LOC",d."CATEGORY_ID",d."DOWNTIME_CATEGORY", tc.MAINDRIVE1_TORQUE_PCT, tc.MAINDRIVE1_CURRENT, tc.MAINDRIVE2_TORQUE_PCT, tc.MAINDRIVE2_CURRENT  from 
