--  DDL for View DISCRTV_DS_OPM_DATA
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "DISCRT"."DISCRTV_DS_OPM_DATA" ("LINE", "RAIL", "TEST_DATE", "PROD_DATE", "SKU", "BATCH", "PRODUCT_SIZE", "WEIGHT", "LENGTH", "RESULT", "DEFECTS", "FACTORY_LOC", "FL_ID", "COLOR", "TARGET_WEIGHT", "TARGET_LENGTH", "LATEST_AQL", "WEIGHT_FAIL", "WEIGHT_DIFF", "LENGTH_FAIL", "LENGTH_DIFF") DEFAULT COLLATION "USING_NLS_COMP"  AS 
  select a."LINE",a."RAIL",a."TEST_DATE",a."PROD_DATE",a."SKU",a."BATCH",a."PRODUCT_SIZE",a."WEIGHT",a."LENGTH",a."RESULT",a."DEFECTS",a."FACTORY_LOC",a."FL_ID", b.COLOR, b.TARGET_WEIGHT, b.TARGET_LENGTH, b.LATEST_AQL,
CASE WHEN a.WEIGHT between trim(substr(TARGET_WEIGHT,0,instr(TARGET_WEIGHT,' - ',1,1))) and trim(substr(TARGET_WEIGHT, INSTR( TARGET_WEIGHT, '- ', -1 ) + 1 ))
THEN 0 WHEN TARGET_WEIGHT is null THEN null ELSE 1 END WEIGHT_FAIL,
null WEIGHT_DIFF,
CASE WHEN 
(trim(substr(LENGTH,0,instr(LENGTH,' - ',1,1))) between trim(substr(TARGET_LENGTH,0,instr(TARGET_LENGTH,' - ',1,1))) and trim(substr(TARGET_LENGTH, INSTR( TARGET_LENGTH, '- ', -1 ) + 1 )))
 and
(trim(substr(LENGTH, INSTR( LENGTH, '- ', -1 ) + 1 )) between trim(substr(TARGET_LENGTH,0,instr(TARGET_LENGTH,' - ',1,1))) and trim(substr(TARGET_LENGTH, INSTR( TARGET_LENGTH, '- ', -1 ) + 1 )))
THEN 0 WHEN TARGET_LENGTH is null THEN null ELSE 1 END LENGTH_FAIL,
null LENGTH_DIFF
from DISCRT_OPM_DETAILS a left join 
(select distinct SKU, COLOR, PRODUCT_SIZE, TARGET_WEIGHT, TARGET_LENGTH, LATEST_AQL, FACTORY_LOC, FL_ID  from DISCRT_OPM_MASTER) b
on a.SKU = b.SKU and a.PRODUCT_SIZE = b.PRODUCT_SIZE
;
