  CREATE OR REPLACE FORCE EDITIONABLE VIEW "DISCRT"."DISCRTV_G2_REJECTION_DOWNTIME" ("PROD_DATE", "LINE", "DOWNTIME_MINS", "DOWNTIME_HRS", "DOWNTIME_CATEGORY", "REASON", "DOWNTIME_TYPE", "SKU", "DAILY_OUTPUT", "G2_PCS", "G2_PCT", "REJECT_PCS", "REJECT_PCT", "FACTORY_LOC") DEFAULT COLLATION "USING_NLS_COMP"  AS 
  select GR.PROD_DATE, GR.LINE, DT.DOWNTIME_MINS, DT.DOWNTIME_HRS, DT.DOWNTIME_CATEGORY, DT.REASON, DT.DOWNTIME_TYPE,
	GR.SKU, GR.DAILY_OUTPUT, GR.G2_PCS, GR.G2_PCT, GR.REJECT_PCS, GR.REJECT_PCT, GR.FACTORY_LOC
from (select PROD_DATE, LINE, sum(DOWNTIME_MINS) DOWNTIME_MINS, sum(DOWNTIME_HRS) DOWNTIME_HRS, DOWNTIME_CATEGORY, REASON, DOWNTIME_TYPE 
	from DISCRTV_DOWNTIME group by PROD_DATE, LINE, DOWNTIME_CATEGORY, REASON, DOWNTIME_TYPE) DT,
(select TEST_DATE as PROD_DATE, LINE, SKU, sum(DAILY_OUTPUT) DAILY_OUTPUT, sum(G2_PCS) G2_PCS, 
	sum(REJECT_PCS) REJECT_PCS, avg(G2_PCT) G2_PCT, avg(REJECT_PCT) REJECT_PCT, FACTORY_LOC 
	from DISCRT_G2_REJECTION  group by TEST_DATE, LINE, SKU, FACTORY_LOC) GR
where DT.PROD_DATE(+) = GR.PROD_DATE and DT.LINE(+) = GR.LINE
;
