--  DDL for View DISCRTV_QUALITY_CONTROL
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "DISCRT"."DISCRTV_QUALITY_CONTROL" ("SKU", "BATCH_NO", "LINE", "REWORK_DATE", "PROD_DATE", "ITEM_SIZE", "AQL", "REWORK_REASON", "REWORK_PROCESS", "GLOVE_WEIGHT", "QTY_BEFORE_REWORK", "G2_REJECT_PCS", "G2_REJECT_PCT", "QTY_AFTER_REWORK", "FACTORY_LOC", "FACTORY_HIERARCHY", "GLOVE_WEIGHT_UOM", "REWORK_PCS", "REWORK_PCT", "REWORK_KG", "REWORK_TYPE") DEFAULT COLLATION "USING_NLS_COMP"  AS 
  select SKU, BATCH_NO, LINE, REWORK_DATE, PROD_DATE, ITEM_SIZE, AQL, REWORK_REASON, REWORK_PROCESS, GLOVE_WEIGHT, QTY_BEFORE_REWORK, 
G2_REJECT_PCS, G2_REJECT_PCT, QTY_AFTER_REWORK, FACTORY_LOC, FACTORY_HIERARCHY, GLOVE_WEIGHT_UOM, 
G2_PCS as REWORK_PCS, G2_PCT as REWORK_PCT, G2_KG as REWORK_KG, 'Second Grade' as REWORK_TYPE
from DISCRT_QUALITY_CONTROL
union all
select SKU, BATCH_NO, LINE, REWORK_DATE, PROD_DATE, ITEM_SIZE, AQL, REWORK_REASON, REWORK_PROCESS, GLOVE_WEIGHT, QTY_BEFORE_REWORK, 
G2_REJECT_PCS, G2_REJECT_PCT, QTY_AFTER_REWORK, FACTORY_LOC, FACTORY_HIERARCHY, GLOVE_WEIGHT_UOM, 
REJECT_PCS as REWORK_PCS, REJECT_PCT as REWORK_PCT, REJECT_KG as REWORK_KG, 'Reject' as REWORK_TYPE
from DISCRT_QUALITY_CONTROL
;
--------------------------------------------------------
--  DDL for View DISCRTV_QUALITY_FAIL
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "DISCRT"."DISCRTV_QUALITY_FAIL" ("TEST_DATE", "ANDRESULT", "BARRESULT", "CRITRESULT", "VISUALMAJORRESULT", "VISUALMINORRESULT") DEFAULT COLLATION "USING_NLS_COMP"  AS 
  select test_date, 
count(CASE WHEN andresult = 'Fail' THEN 1 END) AndResult ,
count(CASE WHEN bar_result = 'Fail' THEN 1 END) BarResult,
count(CASE WHEN crit_result = 'Fail' THEN 1 END) CritResult,
count(CASE WHEN vjresult = 'Fail' THEN 1 END) VisualMajorResult,
count(CASE WHEN vnresult = 'Fail' THEN 1 END) VisualMinorResult
from DISCRT_QUALITY_PIVOT group by test_date
;
--------------------------------------------------------
--  DDL for View DISCRTV_QUALITY_PIVOT_DPM
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "DISCRT"."DISCRTV_QUALITY_PIVOT_DPM" ("TEST_DATE", "LINE", "SKU", "PARAM_COUNT", "PARAM_AVG", "PARAM_NAME") DEFAULT COLLATION "USING_NLS_COMP"  AS 
  select test_date, line, product sku, count(test_date) PARAM_COUNT, sum(anddpm)/count(test_date) PARAM_AVG, 'ANDDPM' PARAM_NAME 
from DISCRT_quality_pivot where anddpm > 0 
group by test_date, line, product
union all
select test_date, line, product sku, count(test_date) PARAM_COUNT, sum(bardpm)/count(test_date) PARAM_AVG, 'BARDPM' PARAM_NAME 
from DISCRT_quality_pivot where bardpm > 0 
group by test_date, line, product
union all
select test_date, line, product sku, count(test_date) PARAM_COUNT, sum(crit_dpm)/count(test_date) PARAM_AVG, 'CRITDPM' PARAM_NAME 
from DISCRT_quality_pivot where crit_dpm > 0 
group by test_date, line, product
union all
select test_date, line, product sku, count(test_date) PARAM_COUNT, sum(vjdpm)/count(test_date) PARAM_AVG, 'VJDPM' PARAM_NAME 
from DISCRT_quality_pivot where vjdpm > 0 
group by test_date, line, product
union all
select test_date, line, product sku, count(test_date) PARAM_COUNT, sum(vndpm)/count(test_date) PARAM_AVG, 'VNDPM' PARAM_NAME 
from DISCRT_quality_pivot where vndpm > 0 
group by test_date, line, product
;
